import React, { useState } from "react";
import friendService from "../../services/FriendService";
import { Link, useLocation } from "react-router-dom";
import "./FriendRequest.css";

const FriendRequest = ({ userId }) => {
  const [receiverEmail, setReceiverEmail] = useState("");
  const location = useLocation();

  const handleSendRequest = () => {
    friendService
      .sendFriendRequest(userId, receiverEmail)
      .then((response) => {
        console.log("Friend request sent:", response.data);
        alert("Friend request sent successfully!");
      })
      .catch((error) => {
        console.error("Error sending friend request:", error.response.data);
        alert("Error sending friend request, please try again.");
      });
  };

  return (
    <div>
      <h3 style={{ padding: "10px 10px" }}>Add Friends</h3>
      <p style={{ padding: "10px 10px" }}>Current User ID: {userId} </p>
      <input
        type="text-id"
        className="friends-input"
        value={receiverEmail}
        onChange={(e) => setReceiverEmail(e.target.value)}
        placeholder="Enter Email to send request"
      />
      <button onClick={handleSendRequest} className="btn send-button">
        Send Friend Request
      </button>
      <Link
        to="/friendRequestList"
        className={`btn ${
          location.pathname === "/friendRequestList"
            ? "btn-success"
            : "btn-default"
        } text-decoration-none`}
      >
        Friend Request List
      </Link>
    </div>
  );
};

export default FriendRequest;
