import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import './Forgot.css'

export default function ResetPassword() {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState({});
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChangePassword = async (e) => {
    e.preventDefault();
    let validationErrors = {};
    console.log("Changing password...");

    // Perform login action with email and password
    if (!email || !validateEmail(email)) {
      validationErrors.email =
        "Invalid email. Only @dal.ca addresses are accepted.";
    }

    const passwordErrors = validatePassword(password);
    if (passwordErrors.length > 0) {
      validationErrors.password = passwordErrors;
    }

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    const updatedUser = {
      email: email,
      newPassword: newPassword
    };

    try {
      const response = await fetch(`http://localhost:8085/api/user/reset-password`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(updatedUser)
      });

      if (!response.ok) {
        throw new Error('Failed to update password');
      }

      console.log("Password updated successfully");
      alert("Password updated successfully");
      setEmail('');
      setNewPassword('');
      setError(null);
      navigate("/login"); // Redirect to Login Page
    } catch (error) {
      console.error("Error updating password:", error);
      setError('Failed to update password');
    }
  };

  return (
    <div className="container">
      <div className="paper">
        <h2 className="subtitle">Reset Password</h2>
        <form className="form" onSubmit={handleChangePassword} noValidate autoComplete="off">
          <input
            type="email"
            placeholder="Email"
            className="input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="New Password"
            className="input"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <button type="submit" className="button">Reset Password</button>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
}
